<?php/*<?php add_option( $name, $value, $deprecated, $autoload );*/function scc_add_options() {	add_option('scc_member', '1', '', 'yes');	add_option('scc_embedpdf','1', '', 'yes');	add_option('scc_note','1', '', 'yes');	add_option('scc_pirate','1', '', 'yes');	add_option('scc_youtube','1', '', 'yes');	add_option('scc_signoff','1', '', 'yes');	add_option('scc_signoff_content','Thanks You For reading this post. If you liked this post then you can subscribe to our RSS Feeds.', '', 'yes');	add_option('scc_dashboard_widget', '1', '', 'yes');	add_option('scc_posteditor_metabox', '1', '', 'yes');	/*add_option('scc_','1', '', 'yes');*/}function scc_delete_options() {	delete_option('scc_member');	delete_option('scc_embedpdf');	delete_option('scc_note');	delete_option('scc_pirate');	delete_option('scc_youtube');	delete_option('scc_signoff');	delete_option('scc_signoff_content');	delete_option('scc_dashboard_widget');	delete_option('scc_posteditor_metabox');	/*delete_option('');*/}